/**
 * Parses the externs and main inputs.
 *
 * @return A synthetic root node whose two children are the externs root
 *     and the main root
 */
Node parseInputs() {
  boolean devMode = options.devMode != DevMode.OFF;

  // If old roots exist (we are parsing a second time), detach each of the
  // individual file parse trees.
  if (externsRoot != null) {
    externsRoot.detachChildren();
  }
  if (jsRoot != null) {
    jsRoot.detachChildren();
  }

  // Parse main JS sources.
  jsRoot = IR.block();
  jsRoot.setIsSyntheticBlock(true);

  externsRoot = IR.block();
  externsRoot.setIsSyntheticBlock(true);

  externAndJsRoot = IR.block(externsRoot, jsRoot);
  externAndJsRoot.setIsSyntheticBlock(true);

  if (options.tracer.isOn()) {
    tracker = new PerformanceTracker(jsRoot, options.tracer);
    addChangeHandler(tracker.getCodeChangeHandler());
  }

  Tracer tracer = newTracer(PARSING_PASS_NAME);

  try {
    // Parse externs sources.
    for (CompilerInput input : externs) {
      Node n = input.getAstRoot(this);
      if (hasErrors()) {
        return null;
      }
      externsRoot.addChildToBack(n);
    }

    // Modules inferred in ProcessCommonJS pass.
    if (options.transformAMDToCJSModules || options.processCommonJSModules) {
      processAMDAndCommonJSModules();
    }

    hoistExterns(externsRoot);

    // Check if the sources need to be re-ordered.
    boolean staleInputs = false;
    if (options.dependencyOptions.needsManagement()) {
      for (CompilerInput input : inputs) {
        // Forward-declare all the provided types, so that they
        // are not flagged even if they are dropped from the process.
        for (String provide : input.getProvides()) {
          getTypeRegistry().forwardDeclareType(provide);
        }
      }

      try {
        inputs =
            (moduleGraph == null ? new JSModuleGraph(modules) : moduleGraph)
            .manageDependencies(options.dependencyOptions, inputs);
        staleInputs = true;
      } catch (CircularDependencyException e) {
        report(JSError.make(
            JSModule.CIRCULAR_DEPENDENCY_ERROR, e.getMessage()));
      } catch (MissingProvideException e) {
        report(JSError.make(
            MISSING_ENTRY_ERROR, e.getMessage()));
      } catch (JSModuleGraph.MissingModuleException e) {
        report(JSError.make(
            MISSING_MODULE_ERROR, e.getMessage()));
      }

      // If in IDE mode, we ignore the error and keep going.
      if (hasErrors()) {
        return null;
      }
    }

    hoistNoCompileFiles();

    if (staleInputs) {
      repartitionInputs();
    }

    // Build the AST.
    for (CompilerInput input : inputs) {
      Node n = input.getAstRoot(this);
      if (n == null) {
        continue;
      }

      if (devMode) {
        runSanityCheck();
        if (hasErrors()) {
          return null;
        }
      }

      if (options.sourceMapOutputPath != null ||
          options.nameReferenceReportPath != null) {

        // Annotate the nodes in the tree with information from the
        // input file. This information is used to construct the SourceMap.
        SourceInformationAnnotator sia =
            new SourceInformationAnnotator(
                input.getName(), options.devMode != DevMode.OFF);
        NodeTraversal.traverse(this, n, sia);
      }

      jsRoot.addChildToBack(n);
    }

    if (hasErrors()) {
      return null;
    }
    return externAndJsRoot;
  } finally {
    stopTracer(tracer, PARSING_PASS_NAME);
  }
}

/**
 * Hoists inputs with the @externs annotation into the externs list.
 */
private void hoistExterns(Node externsRoot) {
  boolean staleInputs = false;
  for (CompilerInput input : inputs) {
    if (options.dependencyOptions.needsManagement()) {
      // If we're doing scanning dependency info anyway, use that
      // information to skip sources that obviously aren't externs.
      if (!input.getProvides().isEmpty() || !input.getRequires().isEmpty()) {
        continue;
      }
    }

    Node n = input.getAstRoot(this);

    // Inputs can have a null AST on a parse error.
    if (n == null) {
      continue;
    }

    JSDocInfo info = n.getJSDocInfo();
    if (info != null && info.isExterns()) {
      // If the input file is explicitly marked as an externs file, then
      // assume the programmer made a mistake and throw it into
      // the externs pile anyways.
      externsRoot.addChildToBack(n);
      input.setIsExtern(true);

      input.getModule().remove(input);

      externs.add(input);
      staleInputs = true;
    }
  }

  if (staleInputs) {
    repartitionInputs();
  }
}

public Node getAstRoot(AbstractCompiler compiler) {
  Node root = ast.getAstRoot(compiler);
  // The root maybe null if the AST can not be created.
  if (root != null) {
    Preconditions.checkState(root.isScript());
    Preconditions.checkNotNull(root.getInputId());
  }
  return root;
}

public Node getAstRoot(AbstractCompiler compiler) {
  if (root == null) {
    parse(compiler);
    root.setInputId(inputId);
  }
  return root;
}

private void parse(AbstractCompiler compiler) {
  try {
    logger_.fine("Parsing: " + sourceFile.getName());
    ParserRunner.ParseResult result = ParserRunner.parse(sourceFile, sourceFile.getCode(),
        compiler.getParserConfig(),
        compiler.getDefaultErrorReporter(),
        logger_);
    root = result.ast;
    compiler.setOldParseTree(sourceFile.getName(), result.oldAst);
  } catch (IOException e) {
    compiler.report(
        JSError.make(AbstractCompiler.READ_ERROR, sourceFile.getName()));
  }

  if (root == null || compiler.hasHaltingErrors()) {
    // There was a parse error or IOException, so use a dummy block.
    root = IR.script();
  } else {
    compiler.prepareAst(root);
  }

  // Set the source name so that the compiler passes can track
  // the source file and module.
  root.setStaticSourceFile(sourceFile);
}

void prepareAst(Node root) {
  CompilerPass pass = new PrepareAst(this);
  pass.process(null, root);
}

public void process(Node externs, Node root) {
  if (checkOnly) {
    normalizeNodeTypes(root);
  } else {
    // Don't perform "PrepareAnnotations" when doing checks as
    // they currently aren't valid during sanity checks.  In particular,
    // they DIRECT_EVAL shouldn't be applied after inlining has been
    // performed.
    if (externs != null) {
      NodeTraversal.traverse(
          compiler, externs, new PrepareAnnotations());
    }
    if (root != null) {
      NodeTraversal.traverse(
          compiler, root, new PrepareAnnotations());
    }
  }
}

/**
 * Traverses a node recursively.
 */
public static void traverse(
    AbstractCompiler compiler, Node root, Callback cb) {
  NodeTraversal t = new NodeTraversal(compiler, cb);
  t.traverse(root);
}

/**
 * Traverses a parse tree recursively.
 */
public void traverse(Node root) {
  try {
    inputId = NodeUtil.getInputId(root);
    sourceName = "";
    curNode = root;
    pushScope(root);
    // null parent ensures that the shallow callbacks will traverse root
    traverseBranch(root, null);
    popScope();
  } catch (Exception unexpectedException) {
    throwUnexpectedException(unexpectedException);
  }
}

/**
 * Traverses a branch.
 */
private void traverseBranch(Node n, Node parent) {
  int type = n.getType();
  if (type == Token.SCRIPT) {
    inputId = n.getInputId();
    sourceName = getSourceName(n);
  }

  curNode = n;
  if (!callback.shouldTraverse(this, n, parent)) {
    return;
  }

  if (type == Token.FUNCTION) {
    traverseFunction(n, parent);
  } else {
    for (Node child = n.getFirstChild(); child != null; ) {
      // child could be replaced, in which case our child node
      // would no longer point to the true next
      Node next = child.getNext();
      traverseBranch(child, n);
      child = next;
    }
  }

  curNode = n;
  callback.visit(this, n, parent);
}

