  Iterator<Node> it = scopeRoots.descendingIterator();
  while (it.hasNext()) {
    scope = scopeCreator.createScope(it.next(), scope);
    scopes.push(scope);
  }
  scopeRoots.clear();

  return scope;
}

public Scope createScope(Node n, Scope parent) {
  Scope scope = scopes.get(n);
  if (scope == null) {
    scope = delegate.createScope(n, parent);
